package day12.test;

//bian 遍历字符串

public class Test2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s = "Geralt of Rivia";
		
	
		for(int i=0; i<s.length();i++) {
			char a = s.charAt(i);    //char at 作为索引
			System.out.println(a);
		}

	}

}
